import React, { Component } from 'react';

import Input from '../../../Components/Input/Input';
import Button from '../../../Components/Button/Button';

import classes from './AddDNA.module.css';

class AddDNA extends Component {
    state = {
        DNAForm: {
            sequenceName: {
                name: 'sequence name (Ex. AF016541.1) ',
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Sequence Name'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 8
                },
                valid: false,
                touched: false
            },
            sequenceDescription: {
                name: 'sequence description (Ex. Cloning vector pUC-JK, complete sequence)',
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Sequence Description'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 17
                },
                valid: false,
                touched: false
            },
            sequenceDNA: {
                name: 'sequence (Ex. TCGCGCGTTTCG...)',
                elementType: 'textarea',
                elementConfig: {
                    type: 'text',
                    placeholder: 'DNA Sequence'
                },
                value: '',
                validation: {
                    required: true,
                    validChar: true
                },
                validChar: false,
                touched: false
            }
        }
    }

    addHandler = (event) => {
        event.preventDefault();
        const formData = {};
        for (let formElementIdentifier in this.state.DNAForm) {
            formData[formElementIdentifier] = this.state.DNAForm[formElementIdentifier].value;
        }
        console.log(formData);
        this.props.DNA.push(formData);
    }

    checkValidity(value, rules){

        let isValid = true;
        if(rules.required){
            isValid = value.trim() !== '' && isValid;
        }
        if(rules.minLength){
            isValid = value.length >= rules.minLength && isValid;
        }
        return isValid
    }

    checkValidityChar(value, rules){
        let isValidChar = false;
        if(rules.validChar){
            if(/^[A-Z]+$/.test(value)){
                isValidChar = true;
            }
        }
        return isValidChar
    }


    inputChangedHandler = (event, inputIdentifier) => {
        const updatedForm = {
            ...this.state.DNAForm
        };
        const updatedFormElement = { 
            ...updatedForm[inputIdentifier]
        };
        updatedFormElement.value = event.target.value;
        updatedFormElement.valid = this.checkValidity(updatedFormElement.value,updatedFormElement.validation);
        updatedFormElement.validChar  =this.checkValidityChar(updatedFormElement.value,updatedFormElement.validation);
        updatedFormElement.touched = true;
        updatedForm[inputIdentifier] = updatedFormElement;
        this.setState({DNAForm: updatedForm});

    }

    render(){


        const formElementsArray = [];
        for (let key in this.state.DNAForm) {
            formElementsArray.push({
                id: key,
                config: this.state.DNAForm[key]
            });
        }
        let form = (
            <form onSubmit={this.addHandler}>
                <p className={classes.Label}>Enter DNA name, description and sequence</p>
                {formElementsArray.map(formElement => {
                    if(formElement.config.elementType == 'textarea'){
                        return (
                            <Input 
                                name={formElement.config.name}
                                key={formElement.id}
                                elementType={formElement.config.elementType}
                                elementConfig={formElement.config.elementConfig}
                                value={formElement.config.value}
                                changed={(event) => this.inputChangedHandler(event, formElement.id)}
                                invalid ={!formElement.config.validChar}
                                touched={formElement.config.touched}
                            />
                        )
                    }else{
                        return(
                            <Input 
                                name={formElement.config.name}
                                key={formElement.id}
                                elementType={formElement.config.elementType}
                                elementConfig={formElement.config.elementConfig}
                                value={formElement.config.value}
                                changed={(event) => this.inputChangedHandler(event, formElement.id)}
                                invalid ={!formElement.config.valid}
                                touched={formElement.config.touched}
                            />
                        )
                    }
                })}
                <Button>Submit</Button>
            </form>

        );
        return(
            <div className={classes.FormLayout}>
                {form}
            </div>
        );
    }
}

export default AddDNA;
