import React, { Component } from 'react';

import Input from '../../../Components/Input/Input';

import classes from './AddDNA.module.css';

class AddDNA extends Component {
    state = {
        DNAForm: {
            sequenceName: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Sequence Name'
                }
            },
            sequenceDescription: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Sequence Description'
                }
            },
            sequenceDNA: {
                elementType: 'textarea',
                elementConfig: {
                    type: 'text',
                    placeholder: 'DNA Sequence'
                }
            }
        }
    }

    render(){
        const formElementsArray = [];
        for (let key in this.state.DNAForm) {
            console.log(key);
            console.log(this.state.DNAForm[key]);
            formElementsArray.push({
                id: key,
                config: this.state.DNAForm[key]
            });
        }
        let form = (
            <form>
                {formElementsArray.map(formElement => (
                    <Input 
                        key={formElement.id}
                        elementType={formElement.config.elementType}
                        elementConfig={formElement.config.elementConfig}
                        value={formElement.config.value}
                    />
                ))}



            </form>

        );
        return(
            <div className={classes.C}>
                {form}
            </div>
        );
    }
}

export default AddDNA;
